/* Minimal shim for sb-admin-2 functionality used by the CRM pages.
   This file loads the official sb-admin-2 script from CDN if available or provides small helpers. */
(function(){
  // if real sb-admin loaded, skip
  if(window.SBAdminLoaded) return;
  // try to load from CDN (not official; use a hosted copy if you maintain one)
  var s=document.createElement('script');
  s.src='https://cdn.jsdelivr.net/gh/startbootstrap/sb-admin-2@master/js/sb-admin-2.min.js';
  s.crossOrigin='anonymous';
  s.onload = function(){ window.SBAdminLoaded = true; };
  s.onerror = function(){
    // fallback: very small helpers used by templates
    window.SBAdminLoaded = true;
    window.toggleSidebar = function(){ var sb=document.getElementById('accordionSidebar'); if(sb) sb.classList.toggle('collapsed'); };
  };
  document.head.appendChild(s);
})();
